}

html, body, .wf-container > * {
	font: normal 300 20px / 25px "Open Sans", Helvetica, Arial, Verdana, sans-serif;
	word-spacing: normal;
	color: #000000;
	margin: 0px;
}
a, a:hover { color: #000000; }
.wf-wrap {padding: 0px !important}
.wf-wrap { width: 100% !important; }
.wf-container, .wf-container-main {margin: 0px !important}
#main { padding: 0px !important; }


// MIXIN
@mixin width_styles ($margin-h-l, $margin-h-m, $margin-h-s,
					$margin-w-m, $margin-w-s,
					$lh-xl, $lh-l, $lh-m, $lh-s, $lh-xs,
					$fontsize-xxxl, $fontsize-xxl, $fontsize-xl, $fontsize-l, $fontsize-m, $fontsize-s, $fontsize-xs, $fontsize-xxs, $fontsize-xxxs)
{
	
	& {
		font-size: $fontsize-l-basic !important;
		padding-left: 100px !important;
	}
	.content {
		padding: 0px !important;
		width: 100% !important;
	}

	.row {
		box-sizing: border-box;
		display: block;
		position: relative;
		&:after {
			clear: both; content: ".";
			display: block;
			visibility: hidden;
			line-height: 0;
			height: 0;
		}
		
		&.pads-big, .pads-big {padding: $margin-h-l 0px}
		&.pads-small, .pads-small {padding: $margin-h-m 0px}
		&.pads-top, .pads-top {padding-top: $margin-h-m}
		&.pads-big-top, .pads-big-top {padding-top: $margin-h-l}

		&.bg-grey, .bg-grey {background-color: $bg-grey}
		&.bg-white, .bg-white {background-color: $bg-white}
		&.bg-none, .bg-none {background-color: transparent}

		
		&.element .basic-text a {
			display: inline-block;
			margin-bottom: 15px;
			text-decoration: underline;
			&:first-child {
				margin-top: 20px;
			}
		}
		&.vertical {
			&:before {
				position: absolute;
				height: $margin-w-m;
				display: block;
				width: 1px;
				background-color: #000000;
				content: '';
				margin: 0 auto;
				left: 0;
				right: 0;
				top: -$margin-w-s;
			}
		}
		&.element {
			&.pads-big-top, .pads-big-top {padding-top: $margin-h-l}
			&.pads-small-top, .pads-small-top {padding-top: $margin-h-m}
			&.pads-big-bottom, .pads-big-bottom {padding-bottom: $margin-h-l}
			&.pads-small-bottom, .pads-small-bottom {padding-bottom: $margin-h-m}
			&.margins-big-top, .margins-big-top {margin-top: $margin-h-l}
			&.margins-small-top, .margins-small-top {margin-top: $margin-h-m}
			&.margins-big-bottom, .margins-big-bottom {margin-bottom: $margin-h-l}
			&.margins-small-bottom, .margins-small-bottom {margin-bottom: $margin-h-m}

			.basic-text a:hover {
				color: $color-violet;
			}
		}
		.column {
			box-sizing: border-box;
			&.left-padding-m {padding-left: $margin-w-m}
			&.right-padding-m {padding-right: $margin-w-m}
			&.left-padding-s {padding-left: $margin-w-s}
			&.right-padding-s {padding-right: $margin-w-s}
			
			&.col-sm-12 { width: 100%; float: left; }
			&.col-sm-11 { width: 91.66666667%; float: left; }
			&.col-sm-10 { width: 83.33333333%; float: left; }
			&.col-sm-9 { width: 75%; float: left; }
			&.col-sm-8 { width: 66.66666667%; float: left; }
			&.col-sm-7 { width: 58.33333333%; float: left; }
			&.col-sm-6 { width: 50%; float: left; }
			&.col-sm-5 { width: 41.66666667%; float: left; }
			&.col-sm-4 { width: 33.33333333%; float: left; }
			&.col-sm-3 { width: 25%; float: left; }
			&.col-sm-2 { width: 16.66666667%; float: left; }
			&.col-sm-1 { width: 8.33333333%; float: left; }

			.content-inner {
				.full-col-image {
					height: 100%;
					width: 100%;
					background-size: cover;
					background-position: center;
				}
				.shift-up {
					transform: translateY(30%);
					transition: 1000ms;
				}
				.basic-text {
					ul {
						margin-left: 20px;
						li {
							padding-top: 20px;
							line-height: $lh-s;
							font-size: $fontsize-xxs;
							list-style: circle;
							letter-spacing: 0.5px;
							text-indent: 10px;
						}
					}
					span {
						margin: 0px;
						cursor: pointer;
						display: inline-block;
						&.opened {
							color: #000000;
						}
						&[data-note] {
							color: $color-violet;
							text-decoration: underline;
							cursor: pointer;
						}
					}
					p {
						line-height: $lh-s;
						font-size: $fontsize-xxs;
						letter-spacing: 0.8px;
						.note-show {
							position: absolute;
							display: flex;
							flex-direction: row;
							background-color: #ffffff;
							box-shadow: 0 0 10px rgba(0,0,0,0.5);
							margin-left: 50px;
							.text {
								font-size: calc(#{$fontsize-xxs} * 0.75);
								padding: 10px 15px;
								line-height: $fontsize-xxs;
								max-width: 250px;
								color: #000000;
								cursor: default;
								text-decoration: none;
							}
							.closer {
								cursor: pointer;
								position: relative;
								display: inline-block;
								min-width: 20px;
								height: 20px;
								overflow: hidden;
								background: linear-gradient(to bottom right, #f72c9d, #c71ae7);
								&::before, &::after {
									content: '';
									position: absolute;
									height: 1px;
									width: 50%;
									top: 50%;
									left: 25%;
									background: #ffffff;
								}
								&::before {
									transform: rotate(45deg);
								}
								&::after {
									transform: rotate(-45deg);
								}
								&.rounded {
									&::before, &::after {
										border-radius: 5px;
									}
								}
							}
						}
					}
				}
				.upper-text p {
					line-height: $lh-m;
					font-size: $fontsize-xs;
					text-transform: uppercase;
					font-family: $font-family-reg;
				}
				.img-column {
					width: 100%;
				}

				&.basic-padding {
					padding: $margin-h-m $margin-w-s !important;
				}
				&.basic-padding-sides {
					padding: 0px $margin-w-s !important;
				}
			}

			.block-title {
				&.header {
					font-size: $fontsize-xxl;
					line-height: $lh-xl;
				}
				font-size: $fontsize-xl;
				text-transform: uppercase;
				line-height: $fontsize-xl;
				font-family: $font-family-bold;
				margin-bottom: $margin-h-m;

				&:after {
					margin-top: $margin-h-s;
					height: 2px;
					display: block;
					width: $hr-length;
					background-color: $color-violet;
					content: '';
				}
			}
			.block-subtitle {
				line-height: $lh-s;
				font-size: $fontsize-s;
				text-transform: uppercase;
				font-family: $font-family-reg;
				letter-spacing: 0.5px;
				margin-bottom: $margin-h-m;
			}
		}
	}

