	// Animation Styles
	.filler-animation {
		display: inline-block;
		overflow: hidden;
		z-index: 1;
		color: transparent;
		.line-wrap {
			position: relative;
			display: inline-flex;
			color: transparent;
			transition: color 1ms;
			transition-delay: calc(#{$time} / 2);
			overflow: hidden;
			margin-right: 5px;
			&:after {
				content: '';
				position: absolute;
				width: 100%;
				height: 100%;
				top: 0;
				left: 0;
				background: $main;
				transform: translateX(-101%);
				transform-origin: 0% 50%;
			}
		}
		&-show {
			z-index: 2;
			.line-wrap {
				color: $second;
			}
			.line-wrap:after {
				transform: translateX(101%);
				transition: transform $time ease-in-out;
			}
		}
	}

