	input[type=range]::-webkit-slider-runnable-track {
		height: 2px;
		cursor: pointer;
		animate: 0.2s;
		background: linear-gradient(to bottom right, #f72c9d, #c71ae7);
	}
	input[type=range]::-moz-range-track {
		height: 2px;
		cursor: pointer;
		animate: 0.2s;
		background: linear-gradient(to bottom right, #f72c9d, #c71ae7);
	}
	input[type=range]::-ms-track {
	height: 2px;
	cursor: pointer;
	animate: 0.2s;
	background: transparent;
	}
	input[type=range]::-webkit-slider-thumb {
		height: 12px;
		width: 12px;
		background: #ffffff;
		cursor: pointer;
		-webkit-appearance: none;
		margin-top: -5px;
		border-radius: 50%;
		box-shadow: 1px 1px 1px #000000, 0px 0px 1px #0d0d0d;
	}
	input[type=range]::-moz-range-thumb {
		height: 12px;
		width: 12px;
		background: #ffffff;
		cursor: pointer;
		-webkit-appearance: none;
		margin-top: -5px;
		border-radius: 50%;
		box-shadow: 1px 1px 1px #000000, 0px 0px 1px #0d0d0d;
	}
	input[type=range]::-ms-thumb {
		height: 12px;
		width: 12px;
		background: #ffffff;
		cursor: pointer;
		-webkit-appearance: none;
		margin-top: -5px;
		border-radius: 50%;
		box-shadow: 1px 1px 1px #000000, 0px 0px 1px #0d0d0d;
	}
	input[type=range]::-ms-fill-lower {
		background: linear-gradient(to bottom right, #f72c9d, #c71ae7);
	}
	input[type=range]::-ms-fill-upper {
		background: #c5c5c5;
	}

	.mfp-close {
		top: 5px !important;
		font-size: 0px !important;
		background: linear-gradient(to bottom right, #f72c9d, #c71ae7);
		&::before, &::after {
			content: '';
			position: absolute;
			height: 1px;
			width: 50%;
			top: 50%;
			left: 25%;
			background: #ffffff;
		}
		&::before {
			transform: rotate(45deg);
		}
		&::after {
			transform: rotate(-45deg);
		}
		&.rounded {
			&::before, &::after {
				border-radius: 5px;
			}
		}
	}

