					margin-left: -$margin-w-s !important;
					margin-right: -$margin-w-s !important;
					margin-top: 120px !important;
					&:before, &:after {
						right: $margin-w-m !important;
						left: initial !important;
					}
					p {
						width: calc(100% - 2*#{$margin-w-m}) !important;
						margin-top: 15px !important;
						padding: 50px $margin-w-m 30px !important;
					}
					.person {
						float: left !important;
						padding-left: $margin-w-m !important;
						p {
							padding: 0px 0px 0px 50px !important;
							margin-top: 0px !important;
							width: auto !important;}
					}
				}
			}
		}

		.quote {
			padding-bottom: $margin-h-s !important;
			padding-top: $margin-h-s !important;
			p { padding: 0px !important; }
		}

		.video {
			padding: 0px !important;
			.content-inner {
				margin: 0px -$margin-w-s !important;
			}
		}

		// CASE
		.case {
			.row {
				&:not(.element) {
					.col-sm-6:first-child {
						.content-inner {
							padding-top: 0px !important;
							.header:after { display: none; }
							img {
								display: block !important;
								width: calc(100% + #{$margin-h-m} + #{$margin-h-m});
								max-width: unset !important;
								margin: 0 -$margin-h-m 0 -$margin-h-m;
								padding-bottom: $margin-h-m;
							}
							.site-link { margin: $margin-h-m 0 0 !important; }
							.info {
								padding-top: 10px !important;
								.column {
									&:first-child {
										.basic-text p:after { display: none; }
									}
								}
							}
						}
					}
					.col-sm-6:nth-child(2) {
						&.pic-col {
							margin-bottom: $margin-h-m !important;
							img { display: none !important; }
							.row {
								padding: 0 $margin-w-s !important;
								.services-case {
									margin-top: 0px !important;
									max-width: 100% !important;
								}
							}
						}
					}
				}
				.related_articles {
					article {
						width: 100%;
						height: 200px;
						&:nth-child(2) { display: none; }
						.post-holder {
							height: 200px;
							.logo {
								max-width: 75px;
								max-height: 50px;
								padding: 15px 0px 0px 20px !important;
							}
							.post-title { padding: 0px 0px 15px 20px !important; }
						}
					}
				}
			}
		}

		.note-show {
			margin: 0px -$margin-w-s !important;
			.text { max-width: 100% !important; }
		}
	}
}



